import React from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import data from "./data/data.json";


function BasicExample() {
  return (
    <Router>
      <Switch>
        {Welcome()}
      </Switch>
    </Router>

  );
}

function Welcome() {
  return (
    <div>
      <h2>Welcome to our friends site</h2>
      <Link exact to="/all">See all users</Link>
    </div>
  );
}

function About() {
  return (
    <div>
      <h2>About</h2>
    </div>
  );
}

function Topics({ match }) {
  return (
    <div>
      <h2>Topics</h2>
      <ul>
        {topics.map(topic => (<li><NavLink to={`${match.url}/` + topic.id}>{topic.topic}</NavLink></li>))}
      </ul>
      <Route exact path={match.path} render={() => <h3>Please select a topic.</h3>} />
      <Route path={`${match.path}/:topicId`} render={(props) =>
        <Topic {...props} theTopic={topics.find(t => t.id === props.match.params.topicId)} />}
      />
    </div>
  );
}
const Topic = ({ match, theTopic }) => (
  <div>
    <h3>{match.params.topicId}</h3>
    <p>{theTopic.topic}</p>
  </div>
);

export default BasicExample;
