import React from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import data from "./data/data.json";


function BasicExample() {
  return (
    <Router>
      <Switch>
        <Route exact path="/" component={Welcome} />
        <Route path="/all" component={AllUsers} />
        <Route path="/details/:index" component={UserDetails} render={(props) =>
          <UserDetails {...props} user={data.find(user => user.index === props.match.params.index)} />} />
      </Switch>
    </Router>

  );
}

function Welcome() {
  return (
    <div>
      <h2>Welcome to our friends site</h2>
      <Link exact to="/all">See all users</Link>
    </div>
  );
}

function AllUsers() {
  return (
    <div>
      <h2>All Users</h2>
      {UsersToTable(data.users)}
      <Link exact to="/">Back</Link>
    </div>
  );
}

const UserDetails = ({ match, user }) => {
  return (
    <div>
      <h2>Details about {user.first} {user.last}</h2>
      <Link exact to="/all">Back</Link>
    </div>
  );
}

function UsersToTable(data) {
  return (
    <table>
      <thead>
        <tr>
          <th>Picture</th>
          <th>Name</th>
          <th>Details</th>
        </tr>
      </thead>
      {data.map((user, index) => UserToTableRow(user, index))}
      <tbody>

      </tbody>
    </table>
  );
}

function UserToTableRow(user, index) {
  return (
    <tr>
      <td><img src={user.picture.thumbnail} /></td>
      <td>{user.first} {user.last}</td>
      <td><Link exact to={("/details/" + index)} >Details</Link></td>
    </tr >
  );
}

export default BasicExample;
