import React from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import data from "./data/data.json";


function BasicExample() {
  return (
    <Router>
      <Switch>
        <Route exact path="/" component={Welcome} />
        <Route path="/all" component={AllUsers} />
        <Route path="/details/:index" component={UserDetails} />
      </Switch>
    </Router>

  );
}

function Welcome() {
  return (
    <div>
      <h2>Welcome to our friends site</h2>
      <Link exact to="/all">See all users</Link>
    </div>
  );
}

function AllUsers() {
  return (
    <div>
      <h2>All Users</h2>
      <Link exact to="/">Back</Link>
    </div>
  );
}

function UserDetails() {
  return (
    <div>
      <h2>Details about fn ln</h2>
      <Link exact to="/all">Back</Link>
    </div>
  );
}

export default BasicExample;
