import React, { Component } from 'react';
import { BrowserRouter as Router, Route, NavLink, Switch, Prompt } from "react-router-dom";
import './App.css';

function Header() {
  return (
    <ul className="header">
      <li><NavLink exact activeClassName="active" to="/">Home</NavLink></li>
      <li><NavLink activeClassName="active" to="/products">Products</NavLink></li>
      <li><NavLink activeClassName="active" to="/addBook">Add Book</NavLink></li>
      <li><NavLink activeClassName="active" to="/company">Company</NavLink></li>
    </ul>
  )
};
function Home() {
  return (
    <p>Home</p>
  )
};

function Company() {
  return (
    <p>Company</p>
  )
};

class Product extends Component {
  render() {
    return (
      <React.Fragment>
        <h2>List of Prodcuts</h2>
        <ul>
          {this.props.bookStore.books.map(book => <li>{book.title} - {book.info}</li>)}
        </ul>
      </React.Fragment>
    );
  }
}

function NoMatch() {
  return (
    <h1>404 - Page not found</h1>
  );
}

class AddBook extends Component {
  state = { title: "", info: "", isBlocking: true };

  handleChange = event => {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    this.setState({
      [name]: value,
      isBlocking: event.target.value.length > 0
    });
  }

  handleSubmit = (event) => {
    alert(JSON.stringify(this.state));
    const book = { title: this.state.title, info: this.state.info };
    this.props.bookStore.addBook(book);
    event.preventDefault();
    this.setState({
      isBlocking: false
    });
  }

  render() {
    return (
      <React.Fragment>
        <h2>Add a new Book</h2>
        <form onSubmit={this.handleSubmit}>
          <input name="title" value={this.state.firstName} onChange={this.handleChange} placeholder="Title" /><br />
          <input name="info" value={this.state.firstName} onChange={this.handleChange} placeholder="Info" /><br />
          <input type="submit" value="Add Book" />
          <p>Blocking? {this.setState.isBlocking ? "Yes, click a link or the back button" : "Nope"}</p>
          <Prompt when={this.setState.isBlocking} message={() => "You have not save the book! Progress will be lost with navigating to another page!"} />
        </form>
      </React.Fragment>
    );
  }

  // render() {
  //   let { isBlocking } = this.state;

  //   return (
  //     <form onSubmit={event => {
  //       event.preventDefault();
  //       event.target.reset();
  //       this.setState({
  //         isBlocking: false
  //       });
  //     }}>
  //       <Prompt when={isBlocking} message={location => `Are you sure you want to go to ${location.pathname}`} />
  //       <p>Blocking?{" "}{isBlocking ? "Yes, click a link or the back button" : "Nope"}</p>
  //       <p><input size="50" placeholder="type something to block transitions" onChange={event => {
  //         this.setState({
  //           isBlocking: event.target.value.length > 0
  //         });
  //       }}
  //       /></p>
  //       <p><button>Submit to stop blocking</button></p>
  //     </form>
  //   );
}

class App extends Component {
  render() {
    return (
      <Router >
        <div>
          <Header />
          <Switch>
            <Route exact path="/" render={() => <Home />} />
            <Route path="/products" render={() => <Product bookStore={this.props.bookStore} />} />
            <Route path="/addBook" render={() => <AddBook bookStore={this.props.bookStore} />} />
            <Route path="/company" render={() => <Company />} />
            <Route component={NoMatch} />
          </Switch>
        </div>
      </Router>

    );
  }
}

export default App;
