import React, { Component } from 'react';
import { BrowserRouter as Router, Route, NavLink, Switch } from "react-router-dom";
import './App.css';

function Header() {
  return (
    <ul className="header">
      <li><NavLink exact activeClassName="active" to="/">Home</NavLink></li>
      <li><NavLink activeClassName="active" to="/products">Products</NavLink></li>
      <li><NavLink activeClassName="active" to="/addBook">Add Book</NavLink></li>
      <li><NavLink activeClassName="active" to="/company">Company</NavLink></li>
    </ul>
  )
};
function Home() {
  return (
    <p>Home</p>
  )
};

function Company() {
  return (
    <p>Company</p>
  )
};

class Product extends Component {
  render() {
    return (
      <React.Fragment>
        <h2>List of Prodcuts</h2>
        <ul>
          {this.props.bookStore.books.map(book => <li>{book.title} - {book.info}</li>)}
        </ul>
      </React.Fragment>
    );
  }
}

function NoMatch(props) {
  return (
    <h1>404 - Page not found</h1>
  );
}

class AddBook extends Component {
  render() {
    return (
      <React.Fragment>
        <h2>Add a new Book</h2>
        <input id="title" type="text" placeholder="Title" /><br />
        <input id="info" type="text" placeholder="Info" /><br />
        <button onclick={addBook(this.props.bookStore)}>Add book</button>
      </React.Fragment>
    );
  }
}

function addBook(bookStore) {
  const title = document.getElementById("title").value;
  const info = document.getElementById("info").value;
  const book = { title: title, info: info };
  bookStore.addBook(book);
}

class App extends Component {
  render() {
    return (
      <Router >
        <div>
          <Header />
          <Switch>
            <Route exact path="/" render={() => <Home />} />
            <Route path="/products" render={() => <Product bookStore={this.props.bookStore} />} />
            <Route path="/addBook" render={() => <AddBook bookStore={this.props.bookStore} />} />
            <Route path="/company" render={() => <Company />} />
            <Route component={NoMatch} />
          </Switch>
        </div>
      </Router>

    );
  }
}

export default App;
